parameters.jsCode:
// Extract NPS data and calculate urgency
const npsData = $input.first().json;

// Simple risk calculation
const urgencyLevel = npsData.score <= 3 ? 'CRITICAL' : 'HIGH';
const riskScore = 10 - npsData.score;

// Build alert data
const alertData = {
  // NPS Data
  nps_score: npsData.score,
  nps_comment: npsData.comment || 'No comment provided',
  submission_time: new Date().toISOString(),

  // Identifiers from webhook
  visitor_id: npsData.visitor_id || 'unknown',
  visitor_email: npsData.visitor_email || npsData.email || 'unknown@example.com',
  visitor_name: npsData.visitor_name || npsData.name || 'Unknown User',
  account_id: npsData.account_id || 'unknown',
  account_name: npsData.account_name || 'Unknown Account',

  // Risk Assessment
  urgency_level: urgencyLevel,
  risk_score: riskScore,

  // Alert emoji based on severity
  alert_emoji: npsData.score === 0 ? 'ðŸš¨' : (npsData.score <= 3 ? 'âš ï¸' : 'âš¡')
};

return alertData;

