    {
      "type": "section",
      "text": {
        "type": "mrkdwn",
        "text": "*Customer Feedback:*\n> {{$json.nps_comment}}"
      }
    },
    {
      "type": "section",
      "text": {
        "type": "mrkdwn",
        "text": "*Submitted:* {{$json.submission_time}}"
      }
    },
    {
