// Process and format Pendo data from MCP responses
const allData = $input.all();

// Initialize default values
let pagesData = [];
let accountsData = [];
let visitorsData = [];
let featureUsageData = [];
let hasErrors = false;
let errorMessages = [];

// Group data by index position from merge node
if (allData.length >= 4) {
  // Data comes in order: pages, accounts, visitors, features
  try {
    // Process pages data (index 0)
    if (allData[0]?.json?.error) {
      hasErrors = true;
      errorMessages.push('Pages: ' + allData[0].json.error.message);
    } else if (allData[0]?.json?.result?.content?.[0]?.text) {
      pagesData = JSON.parse(allData[0].json.result.content[0].text);
    }
  } catch (e) {
    console.error('Error parsing pages data:', e);
  }

  try {
    // Process accounts data (index 1)
    if (allData[1]?.json?.error) {
      hasErrors = true;
      errorMessages.push('Accounts: ' + allData[1].json.error.message);
    } else if (allData[1]?.json?.result?.content?.[0]?.text) {
      accountsData = JSON.parse(allData[1].json.result.content[0].text);
    }
  } catch (e) {
    console.error('Error parsing accounts data:', e);
  }

  try {
    // Process visitors data (index 2)
    if (allData[2]?.json?.error) {
      hasErrors = true;
      errorMessages.push('Visitors: ' + allData[2].json.error.message);
    } else if (allData[2]?.json?.result?.content?.[0]?.text) {
      visitorsData = JSON.parse(allData[2].json.result.content[0].text);
    }
  } catch (e) {
    console.error('Error parsing visitors data:', e);
  }

  try {
    // Process feature usage data (index 3)
    if (allData[3]?.json?.error) {
      hasErrors = true;
      errorMessages.push('Features: ' + allData[3].json.error.message);
    } else if (allData[3]?.json?.result?.content?.[0]?.text) {
      featureUsageData = JSON.parse(allData[3].json.result.content[0].text);
    }
  } catch (e) {
    console.error('Error parsing feature usage data:', e);
  }
}

// Create summary report with safe data access
const report = {
  timestamp: new Date().toISOString(),
  hasErrors: hasErrors,
  errorMessages: errorMessages,
  summary: {
    total_pages: pagesData.length,
    total_accounts: accountsData.length,
    total_visitors: visitorsData.length,
    features_tracked: featureUsageData.length
  },
  top_pages: pagesData.slice(0, 5).map(page => ({
    name: page.name || 'Unknown',
    url: page.url || '#',
    views: page.views || 0
  })),
  active_accounts: accountsData.slice(0, 3).map(account => ({
    id: account.id || 'unknown',
    name: account.name || 'Unknown Account',
    mrr: account.metadata?.mrr || 0
  })),
  visitor_insights: {
    new_visitors: visitorsData.filter(v => v.metadata?.is_new).length,
    returning_visitors: visitorsData.filter(v => !v.metadata?.is_new).length
  },
  top_features: featureUsageData.slice(0, 5).map(feature => ({
    name: feature.feature_name || 'Unknown Feature',
    usage_count: feature.usage_count || 0,
    unique_visitors: feature.unique_visitors || 0
  }))
};

return { json: report };

parameters.markdown:
# Pendo Analytics Report

**Generated:** {{$json.timestamp}}

{{#if $json.hasErrors}}
## âš ï¸ API Connection Issues
The following errors occurred while fetching data:
{{$json.errorMessages.map(msg => `- ${msg}`).join('\n')}}

**Note:** The report below shows placeholder data. To get real data:
1. Ensure the Pendo MCP server is running with valid API credentials
2. Or enable "Pin Data" mode to use the mock data included in the workflow
{{/if}}

## Summary
- **Pages:** {{$json.summary.total_pages}}
- **Accounts:** {{$json.summary.total_accounts}} 
- **Visitors:** {{$json.summary.total_visitors}}
- **Features Tracked:** {{$json.summary.features_tracked}}

## Top Pages
{{$json.top_pages.length > 0 ? $json.top_pages.map(page => `- **${page.name}**: ${page.views} views\n  URL: ${page.url}`).join('\n') : 'No page data available'}}

## Active Accounts
{{$json.active_accounts.length > 0 ? $json.active_accounts.map(account => `- ${account.name} (ID: ${account.id}) - MRR: $${account.mrr}`).join('\n') : 'No account data available'}}

## Visitor Insights
- New visitors: {{$json.visitor_insights.new_visitors}}
- Returning visitors: {{$json.visitor_insights.returning_visitors}}

## Top Features
{{$json.top_features.length > 0 ? $json.top_features.map(feature => `- **${feature.name}**: ${feature.usage_count} uses by ${feature.unique_visitors} unique visitors`).join('\n') : 'No feature data available'}}

